import React, {useEffect,useState} from 'react'
import axios from 'axios';
import {Link } from "react-router-dom";

const Home = () => {

  useEffect( () => {
    window.scrollTo(0, 0);
    alluser();
  }, []); 
  // http://localhost/bor/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/apicrud/users.php
  const [isuser, setuser] = useState([]);
  const alluser = async (ids) => {
    try {
      axios.get(`http://localhost/bor/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/users.php`)
      .then(res => {
        console.log(res.data.userlist.userdata)
        setuser(res.data.userlist.userdata);
      })
    } catch (error) { throw error;}    
  }

  const deleteConfirm = (id) => {
    if (window.confirm("Are you sure?")) {
      deleteUser(id);
    }
  };
  const deleteUser = async (id) => {
    try {
      axios.post(`http://localhost/bor/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/deleteusers.php`, { 
        userids: id,
      })
      .then(res => {
        setuser([]);
        alluser();
        return;
       })
    } catch (error) { throw error;}    
  }

  return (
    <div>
    <Link to="/insert" className='btn'> Create User </Link>
    {isuser.map((item,index)=>(
    <div className="list" key={item.id}>
      <p>Name: {item.id}</p>
      <p>Name: {item.name}</p>
      <p>Email: {item.email}</p>
      <p>Mobile Phone: {item.mobile}</p>
      <Link  to={`edit/${item.id}`} className="btn default-btn"> Edit </Link>
      <p onClick={() => deleteConfirm(item.id)} className="btn default-btn"> Delete </p> 
    </div>
  ))}
    </div>
  )
}

export default Home
