import React, {useEffect,useState} from 'react'
import axios from 'axios';
import {Link } from "react-router-dom";
import './Card.css'
const Home = () => {

  useEffect( () => {
    window.scrollTo(0, 0);
    alluser();
  }, []); 
  // http://localhost/bor/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/apicrud/users.php
  const [isuser, setuser] = useState([]);
  const alluser = async (ids) => {
    try {
      axios.get(`http://localhost/borCopy/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/users.php`)
      .then(res => {
        // console.log(res.data.userlist.userdata)
        setuser(res.data.userlist.userdata);
      })
    } catch (error) { throw error;}    
  }

  const deleteConfirm = (id) => {
    if (window.confirm("Are you sure?")) {
      deleteUser(id);
    }
  };
  const deleteUser = async (id) => {
    try {
      axios.post(`http://localhost/borCopy/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/users.php`, { 
        userids: id,
      })
      .then(res => {
        setuser([]);
        alluser();
        return;
       })
    } catch (error) { throw error;}    
  }

  return (
    <div>
    <Link to="/insert" className='btn'> Create User </Link>
    <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Mobile</th>
                        <th>Actions</th>
                    </tr>
                </thead>
    {isuser.map((item,index)=>(
    <tr className="list" key={item.id}>
      <td> {item.id}</td>
      <td> {item.contract_name}</td>
      <td> {item.signing_date}</td>
      <td> {item.expiration_date}</td>
      <td><Link  to={`edit/${item.id}`} className="button-3"> Edit </Link>
      <Link  to={`view/${item.id}`} className="button-3"> View </Link>
      <a onClick={() => deleteConfirm(item.id)} className="button-44"> Delete </a> </td>
    </tr>
  ))}
  
  </table>
  <div class="features">
      <div class="feature feature-one">
        <h2 class="feature__title">Supervisor</h2>
        <p class="feature__desc">Monitors activity to identify project roadblocks</p>
        {/* <img class="feature__img" src="https://kellychi22.github.io/frontend-mentor-solutions/10-four-card-feature-section/images/icon-supervisor.svg" alt=""/> */}
      </div>
      <div class="wrapper">
        <div class="feature feature-two">
          <h2 class="feature__title">Team Builder</h2>
          <p class="feature__desc">Scans our talent network to create the optimal team for your project</p>
          {/* <img class="feature__img" src="https://kellychi22.github.io/frontend-mentor-solutions/10-four-card-feature-section/images/icon-team-builder.svg" alt=""/> */}
        </div>
        <div class="feature feature-three">
          <h2 class="feature__title">Karma</h2>
          <p class="feature__desc">Regularly evaluates our talent to ensure quality</p>
          <img class="feature__img" src="https://kellychi22.github.io/frontend-mentor-solutions/10-four-card-feature-section/images/icon-karma.svg" alt=""/>
        </div>
      </div>
      <div class="feature feature-four">
        <h2 class="feature__title">Calculator</h2>
        <p class="feature__desc">Uses data from past projects to provide better delivery estimates</p>
        <img class="feature__img" src="https://kellychi22.github.io/frontend-mentor-solutions/10-four-card-feature-section/images/icon-calculator.svg" alt=""/>
      </div>
    </div>


    </div>
  )
}

export default Home
