import React,{useState} from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

const Insert = () => {
  const navigate = useNavigate();
  const [userInfo, setuserInfo] = useState({
    contract_name: '',
    signing_date: '',
    expiration_date: '',
    total_cost: '',
    legal_office_name: '',
    employee_number: '',
    service_items: '',
    amount: '',
    warranty_start_date: '',
    warranty_end_date: '',
    company_id: '',
   


  });
  const onChangeValue = (e) => {
    setuserInfo({
      ...userInfo,
      [e.target.name]:e.target.value
    });
  } 
  // Inserting a new user into the Database.
  const submitUser = async(event) => {
    try {
      event.preventDefault();
      event.persist();
     
      axios.post(`http://localhost/bor/React-CRUD-operations-using-PHP-API-and-MySQLi-main/apicrud/addusers.php`, { 
        contract_name: userInfo.contract_name,
        signing_date: userInfo.signing_date,
        expiration_date: userInfo.expiration_date,
        total_cost: userInfo.total_cost,
        legal_office_name: userInfo.legal_office_name,
        employee_number: userInfo.employee_number,
        service_items: userInfo.service_items,
        amount: userInfo.amount,
        warranty_start_date: userInfo.warranty_start_date,
        warranty_end_date: userInfo.warranty_end_date,
        company_id: userInfo.company_id,



      })
      .then(res => {
        console.log(res.data);
        navigate(`/`);
        return;
       })
    } catch (error) { throw error;}    
  };

return (
  <form className="insertForm" onSubmit={submitUser}>
    <h2> Add Form </h2>
    <label htmlFor="_name">Contract Name</label>
    <input
      type="text"
      id="_name"
      name="contract_name"
      onChange={onChangeValue}
      placeholder="Enter contract name"
      autoComplete="off"
      required
    />

    <br /> <br />
    <label htmlFor="_total_cost">total cost</label>
    <input
      type="text"
      id="_total_cost"
      name="total_cost"
      onChange={onChangeValue}
      placeholder="Enter the total cost"
      autoComplete="off"
      required
    />
    <br /> <br />


    <label htmlFor="_legal_office_name">legal office name</label>
    <input
      type="text"
      id="_legal_office_name"
      name="legal_office_name"
      onChange={onChangeValue}
      placeholder="Enter office name"
      autoComplete="off"
      required
    />
    <br /> <br />


    <label htmlFor="_employee_number">employee number</label>
    <input
      type="text"
      id="_employee_number"
      name="employee_number"
      onChange={onChangeValue}
      placeholder="Enter office name"
      autoComplete="off"
      required
    />
    <br /> <br />


    
    <label htmlFor="_service_items">service_items</label>
    <input
      type="text"
      id="_service_items"
      name="service_items"
      onChange={onChangeValue}
      placeholder="Enter office name"
      autoComplete="off"
      required
    />
    <br /> <br />
    <input type="submit" value="Insert" />
  </form>
);
};

export default Insert;
